import { defineNuxtModule, createResolver, addPlugin, addImports } from '@nuxt/kit';

const name = "@hypernym/nuxt-anime";
const version = "2.1.0";
const configKey = "anime";

const module = defineNuxtModule({
  meta: {
    name,
    version,
    configKey,
    compatibility: {
      nuxt: ">=3.0.0"
    }
  },
  defaults: {
    provide: true,
    autoImport: true
  },
  setup(options, nuxt) {
    const { provide, composables, autoImport } = options;
    const { resolve } = createResolver(import.meta.url);
    const composablesImport = resolve("./runtime/composables");
    nuxt.options.build.transpile.push(resolve("./runtime"));
    if (provide)
      addPlugin(resolve("./runtime/plugin"));
    if (composables)
      nuxt.options.alias["#anime"] = composablesImport;
    if (autoImport && composables)
      addImports([{ name: "useAnime", from: composablesImport }]);
  }
});

export { module as default };
